mapper.select:
select * from t_sales


        userid=#{userid}


        and name like concat('%',#{name},'%')


        and progress=#{progress}


        and date(lasttime)>=#{startdate}


        and date(lasttime) <=#{enddate}


order by id desc
limit ${start},${length}

SELECT count(*) FROM t_sales


        userid = #{userid}


        and name like concat('%',#{name},'%')


        and progress = #{progress }


        and date(lasttime) >= #{startdate}


        and date(lasttime) <= #{enddate}
